INSERT INTO `news` VALUES ('56', '人文社会科学系推荐优秀团员作为党组织培养发展对象工作细则', '<DIV> <P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: center; mso-line-height-rule: exactly\" align=center><B><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">人文社会</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">科学系推荐优秀团员作为</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 14pt\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></B></P> <P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: center; mso-line-height-rule: exactly\" align=center><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">党组织培养发展对象工作细则</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 14pt\"><o:p></o:p></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><o:p>&nbsp;</o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">遵照中共中央组织部、共青团中央《关于进一步做好推荐优秀团员作为党的发展对象工作的意见》，为了进一步做好我系“推优”工作，使之规范化、制度化，保证“推优”工作的质量，特制定本细则。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">一 </SPAN></B><B><SPAN style=\"FONT-SIZE: 15pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">、“推优”条件：</SPAN></B><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36.75pt; TEXT-INDENT: -15.75pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly; tab-stops: list 36.75pt; mso-list: l0 level1 lfo1\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt; mso-bidi-font-family: 仿宋_GB2312\"><SPAN style=\"mso-list: Ignore\">1、<SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp; </SPAN></SPAN></SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">积极要求进步，积极向党组织靠拢，已递交入党申请书达一年以上；（新生至少半年）<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36.75pt; TEXT-INDENT: -15.75pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly; tab-stops: list 36.75pt; mso-list: l0 level1 lfo1\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt; mso-bidi-font-family: 仿宋_GB2312\"><SPAN style=\"mso-list: Ignore\">2、<SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp; </SPAN></SPAN></SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">执行党的基本路线，在思想上、行动上同党中央保持一致；<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36.75pt; TEXT-INDENT: -15.75pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly; tab-stops: list 36.75pt; mso-list: l0 level1 lfo1\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt; mso-bidi-font-family: 仿宋_GB2312\"><SPAN style=\"mso-list: Ignore\">3、<SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp; </SPAN></SPAN></SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">遵守校纪校规，具有良好的思想道德品质；<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36.75pt; TEXT-INDENT: -15.75pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly; tab-stops: list 36.75pt; mso-list: l0 level1 lfo1\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt; mso-bidi-font-family: 仿宋_GB2312\"><SPAN style=\"mso-list: Ignore\">4、<SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp; </SPAN></SPAN></SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">学习目的明确，刻苦认真，各科成绩均在及格以上，总成绩为良好（小班为前<SPAN lang=EN-US>15名，大班为前20名），综合测评成绩在班上前15名；<o:p></o:p></SPAN></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36.75pt; TEXT-INDENT: -15.75pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly; tab-stops: list 36.75pt; mso-list: l0 level1 lfo1\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt; mso-bidi-font-family: 仿宋_GB2312\"><SPAN style=\"mso-list: Ignore\">5、<SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp; </SPAN></SPAN></SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">获得学院（或系）评先评优表彰一次；<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36.75pt; TEXT-INDENT: -15.75pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly; tab-stops: list 36.75pt; mso-list: l0 level1 lfo1\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt; mso-bidi-font-family: 仿宋_GB2312\"><SPAN style=\"mso-list: Ignore\">6、<SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp; </SPAN></SPAN></SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">积极参加青年志愿者行动、社会实践活动和其它社会活动，工作勤奋、扎实，积极完成团组织交办的各项任务；<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36.75pt; TEXT-INDENT: -15.75pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly; tab-stops: list 36.75pt; mso-list: l0 level1 lfo1\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt; mso-bidi-font-family: 仿宋_GB2312\"><SPAN style=\"mso-list: Ignore\">7、<SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp; </SPAN></SPAN></SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">积极参加业余党校学习活动，具备一定的党的基本知识。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 34.95pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly; mso-para-margin-left: 3.33gd\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">对已经向党组织推优的优秀团员，如在党组织未吸收前，发现有不符合上述条件者，将取消推荐资格，收回推荐表。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">二、“推优”程序：<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">“推优”的程序一般按“团员</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; mso-ascii-font-family: 仿宋_GB2312; mso-bidi-font-size: 10.0pt; mso-fareast-font-family: 仿宋_GB2312\">—</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">团支部</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; mso-ascii-font-family: 仿宋_GB2312; mso-bidi-font-size: 10.0pt; mso-fareast-font-family: 仿宋_GB2312\">—</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">团总支</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; mso-ascii-font-family: 仿宋_GB2312; mso-bidi-font-size: 10.0pt; mso-fareast-font-family: 仿宋_GB2312\">—</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">团委”进行。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">1、个人申请。凡是要求推荐为培养发展对象的团员应提出申请，并书面总结自己的思想、学习、工作情况交班团支部。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">2、团支部讨论。团支部委员会对照“推优”条件提出初步人选，主持召开有80%以上团员参加的推荐会，充分听取群众意见，并且作好会议记录，填写《入党对象推荐表》上报系团总支。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">3、团总支审查。团总支根据团支部推荐意见及掌握的日常表现，召开团总支委员会，对团支部所推荐名单逐一审查，取得半数以上同意的，上报学院团委。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">4、报学院团委审批。审批同意后系团总支及时建党，并将推荐表递交系学生党支部。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">5、“推优”工作每学期开学后的第四周进行一次。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">三、“推优”工作的组织领导：<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>1、各级团组织要切实加强对青年的思想政治教育，经常性向党组织汇报对优秀分子的培养、教育情况，要以对党负责的精神，按照条件做好向党组织的“推优”工作。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">2、党支部要加强对“推优”工作的指导，每年至少两次听取团组织对“推优”工作的汇报，检查“推优”工作情况，发现问题及时解决，并定期向党总支汇报“推优”情况。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">3、党支部制定年度发展计划时，要考虑“推优”情况，对团组织推荐的优秀团员，党支部应列为积极分子加强教育、培养，条件成熟时列为发展对象，对具备党员条件的，应适时吸收入党。<o:p></o:p></SPAN></P></DIV>', '规章制度', '学生管理', '0', null, '人文社会科学系', '2005-06-14 00:00:00', '1366', '0');
INSERT INTO `news` VALUES ('57', '人文社会科学系发展党员工作程序细则', '<DIV><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\"><FONT size=4>　　　　　人文社会科学系发展党员工作程序细则</FONT></SPAN></DIV><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\"><FONT size=4> <DIV>&nbsp;</DIV> <DIV>&nbsp;</DIV> <DIV>　　为了认真执行发展党员工作的有关制度，严格入党手续，力求发展工作制度化、规范化，根据《发展党员工作细则》的规定，发展党员必须经过四个阶段。</DIV> <DIV>&nbsp;&nbsp;&nbsp; 一、对入党积极分子的培养教育</DIV> <DIV>&nbsp;&nbsp;&nbsp; 1、送学院党校学习，并编入系“入党积极分子培训班”学习；</DIV> <DIV>&nbsp;&nbsp;&nbsp; 2、支部确定非党积极分子；（第三周）</DIV> <DIV>3、指定一名正式党员做培养联系人，并吸收听党课，参加党内有关活动，分配一定的社会工作；</DIV> <DIV>&nbsp;&nbsp;&nbsp; 4、入党积极分子每学期需向党支部递交二次思想汇报材料。</DIV> <DIV>&nbsp;&nbsp;&nbsp; 二、确定发展对象</DIV> <DIV>&nbsp;&nbsp;&nbsp; 1、团组织的“推优”；（第四周）</DIV> <DIV>&nbsp;&nbsp;&nbsp; 2、支委会讨论列为发展对象并进行政治审查；</DIV> <DIV>&nbsp;&nbsp;&nbsp; 3、报党总支、组织部预审；</DIV> <DIV>&nbsp;&nbsp;&nbsp; 4、对发展对象，报学院党校进行短期重点培训。（第七周）</DIV> <DIV>&nbsp;&nbsp;&nbsp; 三、接收预备党员</DIV> <DIV>&nbsp;&nbsp;&nbsp; 1、确定两名正式党员作介绍人；</DIV> <DIV>&nbsp;&nbsp;&nbsp; 2、填写《入党志愿书》；</DIV> <DIV>&nbsp;&nbsp;&nbsp; 3、支部大会讨论表决；（第十二周）</DIV> <DIV>&nbsp;&nbsp;&nbsp; 4、总支书记找谈话作进一步的了解，并帮助他提高对党的认识；</DIV> <DIV>&nbsp;&nbsp;&nbsp; 5、系党总支研究同意，上报学院党委审批；</DIV> <DIV>&nbsp;&nbsp;&nbsp; 6、组织入党宣誓。</DIV> <DIV>&nbsp;&nbsp;&nbsp; 四、预备党员转正</DIV> <DIV>&nbsp;&nbsp;&nbsp; 1、预备党员必须至少每季度向党支部汇报一次思想、工作、学习情况，求得帮助，不断进步；</DIV> <DIV>&nbsp;&nbsp;&nbsp; 2、党支部每学期对预备党员的有关情况讨论一次，发现问题及时纠正；</DIV> <DIV>&nbsp;&nbsp;&nbsp; 3、预备期满，本人申请，支部大会讨论表决通过；（第十二周）</DIV> <DIV>&nbsp;&nbsp;&nbsp; 4、经系党总支研究同意，报学院党委审批。</DIV> <DIV><BR>&nbsp;</DIV></FONT></SPAN>', '规章制度', '党务管理', '0', null, '人文社会科学系', '2005-06-14 00:00:00', '1140', '0');
INSERT INTO `news` VALUES ('58', '党总支部工作职责　党支部工作职责', '<DIV> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: center; mso-line-height-rule: exactly\" align=center><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 22pt; FONT-FAMILY: 黑体; mso-ascii-font-family: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">党总支部工作职责</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 22pt; mso-bidi-font-size: 10.0pt; mso-fareast-font-family: 黑体\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US><o:p>&nbsp;</o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 26.15pt; LINE-HEIGHT: 26pt; mso-char-indent-count: 2.18; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">1、保证监督党和国家的方针、政策及学校各项决定在本单位的贯彻执行。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>2、加强党组织的思想、组织、作风建设，具体指导党支部的工作。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>3、领导本单位的思想政治工作，了解、分析党员和师生的思想情况，有针对性地进行思想政治教育。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>4、领导本单位工会、共青团、学生会等群众组织。贯彻党的统战工作方针和知识分子政策，抓好党对本单位统战对象及知识分子的工作。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>5、系党总支部参与讨论和决定本单位教学、科研、行政管理工作中的重要事项。支持本单位行政负责人在其职权范围内独立负责地开展工作。同系行政领导一起，做好本单位干部的选拔、培养、考核、监督工作，以及学生政治辅导员、班导师的配备、管理工作。参与讨论决定本单位师生员工要调动、出国、晋升、奖惩、职称评聘、毕业等方面的工作，并负责政治审查。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: center; mso-line-height-rule: exactly\" align=center><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 22pt; FONT-FAMILY: 黑体; mso-bidi-font-size: 10.0pt\"><o:p>&nbsp;</o:p></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: center; mso-line-height-rule: exactly\" align=center><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 22pt; FONT-FAMILY: 黑体; mso-bidi-font-size: 10.0pt\">党支部工作职责<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><o:p>&nbsp;</o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>1、党支部是党的基层组织，必须加强支部建设，充分发挥党支部的战斗堡垒作用和党员的先锋模范作用，保证党和国家的路线、方针、政策及学校党委、系党总支各项决定的贯彻执行。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>2、党支部必须以马列主义、毛泽东思想和邓小平理论为指导，全面贯彻执行党的基本路线和教育方针，坚持教育必须为社会主义现代化建设服务，必须与生产劳动相结合，培养德、智、体等方面全面发展的社会主义事业的建设者和接班人，保证高等学校改革、发展、稳定工作的顺利进行。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>3、宣传、执行党的方针、政策和上级党组织的决议，团结师生员工，保证教学、科研等各项任务的完成。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>4、加强对党员的教育、管理和监督，定期召开组织生活会，开展批评与自我批评；向党员布置做群众工作和其他工作的任务，并检查执行情况。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>5、培养教育入党积极分子，做好发展党员工作。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>6、经常听取党员和群众的意见和建议，了解、分析师生员工的思想情况，有针对性地做好思想政治工作。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>7、教工党支部要根据教学、科研工作的特点，教育党员、群众增强教书育人的政治责任心，搞好教学工作；鼓励和支持党员群众刻苦钻研业务，勇攀科学高峰；注重青年教师的培养，做好在青年教师中发展党员工作。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 28.65pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\">8、学生党支部要根据当代大学生的特点，及时了解他们的思想状况，反映他们的意见、要求和情绪，有针对性地做好经常性的思想政治工作和德育工作；不断扩大要求入党积极分子队伍，按照党章标准发展新党员，逐步建立一支以学生党员为核心的骨干队伍；指导、支持同级共青团、学生会、班委会开展工作；发挥学生党员的先锋模范作用，影响、带动广大学生努力成为合格的社会主义事业建设者和接班人。<o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 仿宋_GB2312; mso-bidi-font-size: 10.0pt\"><o:p>&nbsp;</o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US><o:p>&nbsp;</o:p></SPAN></P></DIV>', '规章制度', '党务管理', '0', null, '人文社会科学系', '2005-06-14 00:00:00', '2223', '0');
INSERT INTO `news` VALUES ('59', '我系举行毕业生座谈会', '<DIV> <DIV> <DIV> <DIV> <DIV align=left> <DIV> <DIV><SPAN style=\"FONT-SIZE: 22pt; FONT-FAMILY: 黑体; mso-bidi-font-size: 10.0pt\"> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-pagination: widow-orphan; mso-line-height-rule: exactly\" align=left><SPAN style=\"FONT-SIZE: 18pt; FONT-FAMILY: 黑体; mso-font-kerning: 0pt; mso-hansi-font-family: 宋体; mso-bidi-font-family: 宋体\"><FONT face=宋体>&nbsp;&nbsp;&nbsp; <FONT size=4>在毕业生离校之际，为了交流学习、生活心得，对学校和系工作提出合理化建议，我系于６月６日晚在综合教学楼举行了首届毕业生座谈会，参加会议的有系主任廖建平教授、系总支书记陈君生副教授和毕业生代表。大家畅谈大学生活和人生感悟。座谈会非常成功</FONT><FONT size=3>!</FONT></FONT></SPAN></P></SPAN></DIV></DIV></DIV></DIV></DIV></DIV></DIV>', '近期工作', null, '0', null, '人文社会科学系', '2005-06-14 00:00:00', '1004', '0');
INSERT INTO `news` VALUES ('60', '大学生思想品德课“六课型”教学的理论与实践思考', '<DIV> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: center; mso-line-height-rule: exactly\" align=center><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 15pt; FONT-FAMILY: 黑体; mso-ascii-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">大学生思想品德</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 15pt; FONT-FAMILY: 黑体; mso-ascii-font-family: 宋体; mso-hansi-font-family: 宋体; mso-bidi-font-size: 12.0pt\">课“六课型”</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 15pt; FONT-FAMILY: 宋体; mso-bidi-font-size: 12.0pt; mso-fareast-font-family: 黑体\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: center; mso-line-height-rule: exactly\" align=center><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 15pt; FONT-FAMILY: 黑体; mso-ascii-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">教学的理论与实践思考</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-FAMILY: 楷体_GB2312\"><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN><SUP><SPAN lang=EN-US><o:p></o:p></SPAN></SUP></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-FAMILY: 楷体_GB2312\"><o:p>&nbsp;</o:p></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 黑体; mso-bidi-font-size: 12.0pt\">摘　要</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-FAMILY: 楷体_GB2312\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>长期以来，大学生思想品德课教学</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-FAMILY: 楷体_GB2312; mso-hansi-font-family: 宋体\">存在内容教条、课型单一和教学模式僵化等问题，</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-FAMILY: 楷体_GB2312\">以致</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-FAMILY: 楷体_GB2312; mso-hansi-font-family: 宋体\">教学效果欠佳，没有发挥其应有的作用。我们在教学实践中发现，采用“六课型”教学是</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-FAMILY: 楷体_GB2312\">思想品德</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-FAMILY: 楷体_GB2312; mso-hansi-font-family: 宋体\">课教学走出困境和提高教学“魅力”的有效途径。为此，我们应明确思想品德课教学的症结所在，树立现代教育新理念，从理论与实践的结合上掌握“六课型”的教学。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 黑体; mso-hansi-font-family: 宋体; mso-bidi-font-size: 12.0pt\"></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-FAMILY: 宋体\"><o:p>&nbsp;</o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">大学生</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">思想品德课</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">是高校“两课”之一，也是高校对学生进行思想政治教育的主渠道、主阵地和重要环节之一。</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-weight: bold\">由于受传统教学观的影响，长期以来，思想品德</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-bidi-font-weight: bold\">课教学存在内容教条、课型单一和教学模式僵化等问题，</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">使该课程的教学陷入困境<SPAN lang=EN-US>,<SPAN style=\"mso-bidi-font-weight: bold\">教学效果欠佳，学生“不愿学”和“不重视”现象普遍，没有发挥其应有的作用。这一直是教师们感到困惑、烦恼的一大难题，也是思想品德课教学改革难以取得突破性进展的症结所在。我们在教学实践中发现，采用“六课型”教学是</SPAN></SPAN></SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-weight: bold\">思想品德</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-bidi-font-weight: bold\">课教学走出困境和提高教学“魅力”的有效途径。为此，我们在分析教学困境的症结基础上，基于现代教育新理念，从理论与实践的结合上探索了</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">思想品德课</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-bidi-font-weight: bold\">“六课型”的教学。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\"><o:p>&nbsp;</o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: center; mso-line-height-rule: exactly\" align=center><B><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-bidi-font-size: 12.0pt\">一、</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">大学生思想品德</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-bidi-font-size: 12.0pt\">课</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">教学存在的困境与问题</SPAN></B><B><SPAN lang=EN-US style=\"FONT-SIZE: 16pt; FONT-FAMILY: 宋体; mso-bidi-font-size: 12.0pt\"><o:p></o:p></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24pt; LINE-HEIGHT: 26pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p>&nbsp;</o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24.1pt; LINE-HEIGHT: 26pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">1</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">、<SPAN style=\"mso-bidi-font-weight: bold\">“所讲非所想，所教非所需”</SPAN></SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">———</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">内容教条</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></B></P> <P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN style=\"FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">不少大学生把思想品德课当做“政治课”，其主要原因是我们平时多注重书本知识的传授，多进行教条化的宣讲，很少跳出教材的束缚，有些甚至唯教材至上，把教材中的内容当作金科玉律，不敢对教学内容进行充实、调整和更新，使课堂教学内容教条化。这样，教学内容就没有新意，不能很好地反映时代特点和学生实际，从而使学生感到空洞、枯燥，不能打动学生、吸引学生，使思想品</SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">德课教学出现“所讲非所想，所教非所需”的局面。</SPAN><SPAN style=\"FONT-FAMILY: 宋体\"> </SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">2</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">、单纯的<SPAN style=\"mso-bidi-font-weight: bold\">知识传授</SPAN></SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">———</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">目标狭隘</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">教学目标是人的全面发展，而不单单是传授知识。人的全面发展是衡量教育所培养人才质量规格的重要标志。二十一世纪教育的四大支柱是：学会求知、学</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">会共处、学会做事、学会做人。但在思想品德课教学中，教师往往只重在知识的传授，习惯于“注入式”的教学，学生成了被动的接受器，忽视学生自主性思维和道德人格的内化，忽视学生的情感、意志，从而导致学生精力不集中，出现学生“厌学”、教师“难教”局面，教学效果不理想。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24.1pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">3</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">、<SPAN style=\"mso-bidi-font-weight: bold\">刻板、僵化的教学模式</SPAN></SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">———</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">结构程式</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">课堂教学是由一系列环节构成的，教学环节与环节之间的联系应该在不同的课型中有不同的体现，表现为不同的样式。但是，在思想品德课教学中，课堂教学大多是一种刻板、僵化、统一的模式，即把课堂教学程式化了。其实，课堂是动的，不是静的；是活的，不是死的。思想品德课教学不能没有民主、没有人性、没有个性，应根据不同的教学内容、不同的教学方法，采用不同的教学模式。既不能用“唯一”代替“多样”，也不能用“刻板”代替“灵活”。课堂教学应当在一种自由、平等、活跃的课堂气氛中进行。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">4</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">、惟<SPAN style=\"mso-bidi-font-weight: bold\">班级课堂授课</SPAN></SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">———</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">组织形式单一</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">在思想品德课教学中，教学组织形式同其它理论专业课一样，大多数采用班级课堂授课，从课堂到课堂，从书本到教师，从教师到学生，教学组织形式单一。忽视了校内第二课堂和校外第二课堂的教学形式的运用，把班级课堂授课当做惟一的教学组织形式，把教师当作唯一的教学主体。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p>&nbsp;</o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: center; mso-line-height-rule: exactly\" align=center><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">二、大学生思想品德课教学走出困境的理论思考</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><o:p></o:p></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p>&nbsp;</o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">1</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">、现代教育理念</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">───</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">从“知识为本”到“以人为本”</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">中国教育改革的目标是从应试教育走向素质教育，素质教育是以德育为核心，以培养创新精神和实践能力为重点的。传统教育观念认为，课堂教学即传授知识，教育以“知识为本”，因此，思想品德课长期以来形成了教师“讲”、学生“听”的“一言堂”教学模式。现代教育的观念是“以人为本”，主张将人的培养放在第一位，人的发展是教育的出发点和归宿，应把人的和谐与全面发展放在第一位。因此，思想品德课教学重点不是单纯地向学生传授知识，而是培养学生积极进取并形成良好的综合素质和实践创新能力。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">2</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">、现代课堂教学</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">───从“知识课堂”到“生命课堂”<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">课堂教学是现代教育的重要形式，传统的课堂教学模式是把课堂作为“专门的传授知识的场所”，它是“知识为本”或“知识至上”的课堂。它的弱点十分明显，强调应试教育，使人成为知识的奴隶，成为被知识“异化”的人。在“知识课堂”中，教师的角色是知识的传授者，学生的学习以死记硬背、机械训练为主，其教学是一种“操作性教学”，教师在课堂教学中讲得口干舌燥、筋疲力尽，而学生在课堂上听得头脑发胀，昏昏欲睡，其实质是一种低效的教学。</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">思想品德课是教育学生做人的一门课，必须走出“知识课堂”，步入“生命课堂”。“生命课堂”就是在课堂教学中，不是为了单纯传授知识而教学，而是为了人的发展而教学。“生命课堂”改变了惟知识传授至上的做法，成为从单纯追求知识的课程转变为全面引导学生学会学习、学会做人、学会生存的课堂。在“生命课堂”中，教师的主导性、创造性、挑战性、趣味性等融为一体，使教师在教学中充分考虑学生的特点与学习的需要，真正做到因材施教、因人施教。在“生命课堂”中，学生成为学习的主人，学生在课堂中应有自己思维与活动的时间与空间，学生在学习中将体验与兴趣结合起来，将自己的学习方法、价值观与知识的获取结合起来。课堂将由过去“死”的课堂变为“活”的课堂、“静”的课堂变为“动”的课堂、“教”的课堂变为“学”的课堂。在“生命课堂”中，教学过程强调教师与学生、学生与学生之间的相互沟通、相互激励、相互启发，是“活的教学”“有生命的教学”。因此，思想品德课课堂教学要真正达到教学目的，就必须完成</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">从“知识课堂”到“生命课堂”的转变。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">3</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">、创新教育观念</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">───从“单课型”教学到“多课型”教学<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">创新是一个民族兴旺发达的不竭的动力，只有培养具有较强创新精神和创新能力的人才，才能提高我国的综合国力，才能占领新世纪的制高点。进行创新教育，首先要进行教育的创新，教育的创新是推动教育发展的原动力。长期以来，由于受传统教育观念、教育模式的约束，思想品德课教学</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">多采用教师单纯讲解和学生边听课边记笔记的程式化方法，缺少新意，学生不感兴趣，也难以适应现代教育的全新要求，严重妨碍了学生创新精神和创新能力的培养。这就必然促使我们去寻求课堂教学的新模式，要求我们一定要解放思想，在不断更新教学内容的同时，大胆革新教学方法，用灵活的教学形式、教学手段来组织教学。实践证明，从</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">“单课型”教学走向“多课型”教学，</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">是适应新时期教育创新和创新教育的必由之路。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">4</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">、教育平等观念</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">───“教师主导”加“学生主体”<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">教育平等有两层含义：一是说在受教育过程中受教育者一律平等；二是说在教育过程中教育者和受教育者一律平等。坚持教育平等，在</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">思想品德课教学中就要坚持教师和学生在教学中的平等，充分发挥师生积极性，形成教与学的同质合力，即教学相长。教学是人施于人的过程。一方面，在教学中教师对教与学起主导作用，决定着整个教学过程向着某种特定方向发展。另一方面，学生在教学中起主体作用，在教与学的矛盾中，学生的学是矛盾的主要方面，学生是活生生的有思想、有个性的人。因此，教师不可能将知识硬塞进学生的头脑中去，更不可能迫使其对不理解、不相信的东西无条件接受。教师要充分尊重学生的独立人格和个性，改变传统教育中教师完全操纵教学过程的方式，树立“以学生为中心”的指导思想，充分调动学生学习的主动性、积极性，真正以学生为主体。只有“教师主导”加“学生主体”的思想品德课教学，才能充分发挥教学中两个积极性的作用，才能收到最好的教学效果。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p>&nbsp;</o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: center; mso-line-height-rule: exactly\" align=center><B><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">三、大学生思想品德课“六课型”教学实践思考</SPAN></B><B><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><o:p></o:p></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: center; mso-line-height-rule: exactly\" align=center><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><o:p>&nbsp;</o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24pt; LINE-HEIGHT: 26pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">基于现代教育新理念，我们在分析思想品德课教学存在问题的基础上，在大学生《思想道德修养》课教学中，进行了“六课型”（“讲授型”、“研究型”、“辩论型”、“参观型”、“讨论型”、“实践型”）教学改革，并从</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">2002</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">年下半年着手“六课型”教学改革的尝试。通过近二年的“六课型”教学实践，我们认为，<SPAN style=\"mso-bidi-font-weight: bold\">思想品德课</SPAN>“六课型”教学与传统教学相比有突出的优点，我们对成功组织“六课型”教学有了更深刻的体会。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24.1pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">1</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">、思想品德课“六课型”教学与传统教学相比有突出的优点</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24.1pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">（<SPAN lang=EN-US>1）、实现了从</SPAN></SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">“以教师为中心”到</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体\">“</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">以学生为中心”的转变</SPAN></B><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">。“六课型”教学改变了过去以教师为中心，一味灌输的传统教学观念，形成了“以学生为中心”和“以人为本”的课堂教学新观念。在“六课型”教学中，思想品德课教师把自己定位为一个引导者的角色，增强了教学内容的针对性，营造了良好的教学氛围，更有利于学生进行自我思考、自我教育。思想品德课“六课型”教学，既增强了学生学习的科学性、自主性、实践性、探索性，又活跃了学习气氛，从而使学生学有所得。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24.1pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">（</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">2</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">）、实现了从“以知识为中心</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">”</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">到“以能力为中心”的转变。</SPAN></B><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">大学生思想品德课教学的最终目的不是在于给学生灌输了多少知识，而是通过教学，通过教师的讲解和引导，使学生树立正确的世界观、人生观和价值观，提高学生分析问题和解决问题的能力。实践证明，大学生思想品德课“六课型”教学既培养了学生学习的主动性和积极性，又使学生在自主学习的基础上，提高了综合能力。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24.1pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">（</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">3</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">）、改革了教学方式，增强了教学感染力和吸引力。</SPAN></B><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">大学生思想品德课“六课型”教学摒弃了传统的说教式教学，采用灵活多样的易被学生接受的方式来进行，让学生参与到教学当中，同时，针对课程内容实际、学生实际，选用不同课型的教学，改进了教学方式，并充分运用现代化的多媒体教学手段，增强了教学的针对性、感染力和吸引力，激发了学生学习的兴趣。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24.1pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">（</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">4</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">）、扩大了“课堂”空间，在活动和实践中强化了思想素质。</SPAN></B><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">如果单纯地把学生局限在课本上、课堂内，那么学生创新精神和实践能力的培养，学生全面素质的提高，是难以真正达到的。思想品德课“六课型”教学与实践结合起来，扩大了“课堂”空间，并积极创造机会，鼓励学生走出课堂，走出校园，深入社会，在活动和实践中强化了思想品德修养。大学生思想品德课“六课型”教学真正实现了校内课堂与校内第二课堂和校外第二课堂的有机地结合。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24.1pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">2</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">、<SPAN style=\"mso-bidi-font-weight: bold\">成功组织“六课型”教学的深刻的体会</SPAN></SPAN></B><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24.1pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">（</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">1</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">）、做好课型选择。</SPAN></B><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">“六课型”中每一种课型都有其优势和局限，并不是每一课型都适用每门课程中的每一章、每一节的教学。因此，做好课型选择，是思想品德课“六课型”教学取得成功的前提。对于不同课程、不同教材、同一课程中的不同章节内容，要采用不同的课型。课型的选择一定要与教学目的相配，要依据时间、地点、条件的不同，选择不同的课型。课型的选择要有利于激发学生学习的兴趣，调动学生在教学过程中的主观能动性，能更好地培养学生分析问题和解决问题的能力。课型选择切忌追求形式的新颖，要把追求最佳教学效果放在首位。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24.1pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">（</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">2</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">）、重视教学设计。</SPAN></B><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">“六课型”教学的成功与否，从一定意义上说取决于教师教学设计的成功与否。俗话说，予则立，不予则废。其实，搞好“六课型”教学并无捷径可寻，需要教师进行精心准备、周密策划，要用“心”去做。只有重视教学设计，才能突出重点，攻破难点，抓住教学的突破口和关键环节；才能做到生动、活泼、富有成效；才能实现“认知、情感、能力三元结构目标”。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24.1pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">（</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">3</SPAN></B><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">）、激发学生参与。</SPAN></B><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">教学是教师“教”和学生“学”的双边活动。“六课型”教学打破传统意义上的教学，更强调师生共同参与、平等参与，强调师生应彼此尊重、平等相待，要求教师不扣帽子、不打棍子、不揪辫子。否则，“六课型”教学不可能收到好的效果。因此，教师要善于调动学生的主动参与性，激发和诱导学生参加“六课型”教学的欲望。</SPAN><B style=\"mso-bidi-font-weight: normal\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></B></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><B><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">（</SPAN></B><B><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">4</SPAN></B><B><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">）、讲究课堂驾驭艺术</SPAN></B><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">。“六课型”教学能否取得成功及成效的大小，与教师能否恰当引导、善于驾驭课堂关系重大。这是一门艺术，要求教师要有较高的驾驭能力。如在“参与型”教学课堂讨论时，当学生不能主动发言的时候，教师要善于启发学生积极思维，寻找突破口，选择合适的学生带头发言，以便打破僵局。当课堂讨论激烈时，教师要善于抓住关键，将课堂引向深入，还要排除干扰，不致因纠缠个别枝节问题，占用宝贵的时间，降低讨论质量。讨论到一定时候，教师要善于适时归纳总结，力求做到中心突出、语言简练、道理明白。这样，学生才能印象深刻，达到教学目的。</SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\" align=left><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\"></SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">（本文系湖南省社科基金项目高校思想政治教育课题成果　　　</SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\" align=left><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">　　　　　　　作者：陈君生）</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN lang=EN-US><o:p>&nbsp;</o:p></SPAN></P></DIV>', '科研成果', '科研论文统计', '0', null, '人文社会科学系', '2005-06-14 00:00:00', '1947', '0');
INSERT INTO `news` VALUES ('74', '廖建平教授简介', '<DIV> <DIV> <DIV> <DIV> <DIV> <DIV> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\" align=left><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">&nbsp;&nbsp;&nbsp; 廖建平</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体\">同志</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">，男，</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体\">汉族，</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt\">1963</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">年生，湖南省衡南县人，中共党员，教授，</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体\">湖南省青年骨干教师。<SPAN lang=EN-US>1986</SPAN>年于湖南师范大学政治系<SPAN lang=EN-US>,</SPAN>现</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">主要从事伦理学和逻辑学的教学及伦理学和中国哲学的研究。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 28pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体\">在《人民日报》、《江汉论坛》、《江西省会科学》等刊物上发表<SPAN lang=EN-US>30</SPAN>多篇论文，<SPAN lang=EN-US>2</SPAN>篇获校科研成果二等奖；《“协和万邦”：中国对外关系的文化准则──兼评中国威胁论的失据》、《儒家仪容观及其现代意义》、《王夫之的仪<?xml:namespace prefix = st1 ns = \"urn:schemas-microsoft-com:office:smarttags\" /><st1:PersonName ProductID=\"容与\" w:st=\"on\">容与</st1:PersonName>君子人格关系论》等论文被人大复印资料全文转载；主持省教育厅科研项目<SPAN lang=EN-US>1</SPAN>项；主持衡阳师范学院课题<SPAN lang=EN-US>2</SPAN>项，获衡阳市优秀社会科学成果二等奖2项,衡阳师范学院十佳青年教师、十佳授课教师。现为衡阳师范学院人文社会科学系主任。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 25.6pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; LETTER-SPACING: -0.6pt\">主要著作有《</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体\">君子人格论》（<SPAN lang=EN-US>2001</SPAN>、<SPAN lang=EN-US>12</SPAN>，<SPAN style=\"LETTER-SPACING: -0.6pt\">中国文联出版社）和《</SPAN>人生哲理散论<SPAN style=\"LETTER-SPACING: -0.6pt\">》（</SPAN><SPAN lang=EN-US>2003</SPAN>、<SPAN lang=EN-US>8</SPAN>，<SPAN style=\"LETTER-SPACING: -0.6pt\">当代中国出版社）。</SPAN></SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN lang=EN-US><o:p>&nbsp;</o:p></SPAN></P></DIV></DIV></DIV></DIV></DIV></DIV>', '师资队伍', '教授风采', '0', null, '人文社会科学系', '2005-06-20 00:00:00', '2485', '0');
INSERT INTO `news` VALUES ('75', '唐林生教授简介', '<DIV> <DIV> <DIV><FONT face=华文行楷> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 44pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: center; mso-line-height-rule: exactly; mso-char-indent-count: 2.0\" align=center><?xml:namespace prefix = st1 ns = \"urn:schemas-microsoft-com:office:smarttags\" /><st1:PersonName ProductID=\"唐林生\" w:st=\"on\"><SPAN style=\"FONT-SIZE: 22pt; FONT-FAMILY: 华文行楷\"></SPAN></st1:PersonName></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">唐林生，男，</SPAN><st1:chsdate w:st=\"on\" Year=\"1963\" Month=\"2\" Day=\"14\" IsLunarDate=\"False\" IsROCDate=\"False\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><FONT face=\"Times New Roman\">1963</FONT></SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">年</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><FONT face=\"Times New Roman\">2</FONT></SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">月</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><FONT face=\"Times New Roman\">14</FONT></SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">日</SPAN></st1:chsdate><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">生，湖南祁阳人。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><FONT face=\"Times New Roman\">1983</FONT></SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">年毕业于武汉大学历史系，长期从事教学工作。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">在教学上先后承担了历史、政教共</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><FONT face=\"Times New Roman\">10</FONT></SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">门专业课的教学。如历史专业的《世界近代史》、《世界现代史》、《世界当代史》、《马列原著选读》、《日本史》、《港澳台研究》、《西方政治制度史》、《世界热点问题》，政教专业的《世界近现代史》、《当代世界政治经济与国际关系》等课的教学。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 24pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'\">在科研上，出版学术专著<SPAN lang=EN-US>2</SPAN>部。其中《薄弱环节与现实的社会主义》由《中国文史出版社》出版，《海峡两岸关系史》由湖北人民出版社出版，参编<SPAN lang=EN-US>2</SPAN>部；在《中国宗教》、《江汉论坛》、《江西社会科学》、《求索》等刊物发表论文<SPAN lang=EN-US>30</SPAN>多篇。其中《关于当代民族运动的思考》、《本世纪社会主义运动的历史考察与现实思考》、《“新全球化”—资本主义新阶段论》、《太平军武器装备初探》等多篇文章被《人大复印资料》和《高校文摘》全文复印摘录。获衡阳市社会科学成果二等奖一项。科研方向在社会主义运动上。<SPAN lang=EN-US><o:p></o:p></SPAN></SPAN></P></FONT></DIV></DIV></DIV>', '师资队伍', '教授风采', '0', null, '人文社会科学系', '2005-06-20 00:00:00', '2327', '0');
INSERT INTO `news` VALUES ('76', '管仕福教授简介', '<DIV> <DIV> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 44pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: center; mso-line-height-rule: exactly; mso-char-indent-count: 2.0; mso-pagination: widow-orphan; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto\" align=center><?xml:namespace prefix = st1 ns = \"urn:schemas-microsoft-com:office:smarttags\" /><st1:PersonName ProductID=\"管仕福\" w:st=\"on\"><SPAN style=\"FONT-SIZE: 22pt; FONT-FAMILY: 华文行楷\"></SPAN></st1:PersonName><SPAN style=\"FONT-SIZE: 22pt; FONT-FAMILY: 华文行楷\"><SPAN lang=EN-US><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 28pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: left; mso-line-height-rule: exactly; mso-char-indent-count: 2.0; mso-pagination: widow-orphan; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto\" align=left><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">管仕福，男，汉族，湖南祁东人，生于</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">1954</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">年</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">10</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">月，民盟盟员，教授，</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">1976</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">年</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">9</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">月至</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">1979</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">年</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">9</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">月在祁东草源冲中学任教，</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">1983</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">年</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">7</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">月毕业于湘潭大学历史系。同年来衡阳师范学院工作。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">1989</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">年被评为讲师，</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">1995</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">年</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">12</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">月晋升为副教授，</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">2002</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">年</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">8</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">月晋升为教授。</SPAN><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"> </SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-font-kerning: 0pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 28pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly; mso-char-indent-count: 2.0; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto\"><st1:PersonName ProductID=\"管仕福\" w:st=\"on\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">管仕福</SPAN></st1:PersonName><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">教授长期从事教学工作，有着丰富的教学经验，担任《中共党史》、《毛泽东思想概论》、《中国近代史》等多门课程教学，所授课程因其知识性、趣味性深受学生欢迎。先后出版著作两部（《海峡两岸关系史》、《近代中国人的民主意识与民主建设》），近年来先后完成省级及院级科研课题多项，发表学术论文三十余篇。科研上以中国近现代史和中国政治文化的研究为主，论文多被《高校文科学报文摘》、中国人民大学资料复印中心等国内权威性学术刊物摘录或转载。</SPAN></P></DIV></DIV>', '师资队伍', '教授风采', '0', null, '人文社会科学系', '2005-06-20 00:00:00', '2557', '0');
INSERT INTO `news` VALUES ('77', '张齐政教授简介', '<DIV> <DIV> <DIV> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 27.75pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: center; mso-line-height-rule: exactly; mso-pagination: widow-orphan; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto\" align=center><?xml:namespace prefix = st1 ns = \"urn:schemas-microsoft-com:office:smarttags\" /><st1:PersonName w:st=\"on\" ProductID=\"张齐政\"><SPAN style=\"FONT-SIZE: 22pt; COLOR: black; FONT-FAMILY: 华文行楷; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\"></SPAN></st1:PersonName><SPAN style=\"FONT-SIZE: 22pt; COLOR: black; FONT-FAMILY: 华文行楷; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\"><SPAN lang=EN-US><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 27.75pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: left; mso-line-height-rule: exactly; mso-pagination: widow-orphan; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto\" align=left><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 10.0pt; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">张齐政，男，汉族，湖南辰溪人。<SPAN lang=EN-US>1958</SPAN>年生，中共党员。<SPAN lang=EN-US>1975</SPAN>年参加工作，<SPAN lang=EN-US>1983</SPAN>年毕业于兰州大学历史系，获历史学学士学位，同年分配到衡阳师院工作至今。后又分别就读于云南大学和复旦大学。先后担任过历史系党支部副书记、历史系主任、法学院院长。<SPAN lang=EN-US>1989</SPAN>年被评为讲师，<SPAN lang=EN-US>94</SPAN>年晋升为副教授，并被湖南省教委确定为湖南省高校青年骨干教师，是我校拔尖人才之一，<SPAN lang=EN-US>1999</SPAN>年晋升为教授。<SPAN lang=EN-US>2004</SPAN>年被评为衡阳市首届优秀社会科学专家</SPAN><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: 宋体; mso-bidi-font-size: 10.0pt; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">。</SPAN><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 10.0pt; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">现为衡阳师范学院学报期刊社总编。 中国世界古代史研究会理事，湖南省历史学会常务理事，衡阳市社会科学联合会常务理事。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 28.5pt; LINE-HEIGHT: 26pt; TEXT-ALIGN: left; mso-line-height-rule: exactly; mso-pagination: widow-orphan; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto\" align=left><st1:PersonName w:st=\"on\" ProductID=\"张齐政\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-bidi-font-size: 10.0pt; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">张齐政</SPAN></st1:PersonName><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">教授曾先后担任过三届班主任工作，带过八届教育实习。业务能力强，在教学上先后担任过四门主干课程的教学工作。科研上先后出版学术专著五部</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">(</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">《玛丽</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">·</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">斯图亚特传》</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">1998</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">年吉林人民出版社；《世界上古中古史》</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">1998</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">年黑龙江人民出版社；《南岳寺庙建筑与寺庙文化》</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">1999</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">年花城出版社；《寿山说寿》</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">2000</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">年湖南教育出版社；《汉尼拔与布匿战争》</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">2001</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">年吉林人民出版社。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">)</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 楷体_GB2312; mso-hansi-font-family: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">，</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">发表</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">学术论文四十多篇，主持省级科研课题五项（《中西古典文明比较研究》</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">1995</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">年；《南岳历史文化研究》</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">1996</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">年；《中西古典史学思想研究》</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">1998</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">年；《南岳寺庙土地制度研究》</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">2000</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">年；《“南岳学”研究》</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">2001</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\">年。），获各种科研成果奖十二项，其科研课题涉及历史学、宗教学、经济学、民俗学、社会学等领域，科研论文多篇被中国人民大学报刊资料复印中心全文复印、《新华文摘》等权威刊物摘要或转载，在学术界反响强烈。</SPAN><SPAN style=\"FONT-SIZE: 13.5pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体; mso-fareast-font-family: 隶书\"> </SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt; COLOR: black; FONT-FAMILY: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: 26pt; mso-line-height-rule: exactly\"><SPAN lang=EN-US><o:p>&nbsp;</o:p></SPAN></P></DIV></DIV></DIV>', '师资队伍', '教授风采', '0', null, '人文社会科学系', '2005-06-20 00:00:00', '2576', '0');
INSERT INTO `news` VALUES ('86', '〈君子人格论〉-----廖建平著', '<DIV> <DIV> <DIV> <DIV> <DIV> <DIV> <DIV> <DIV> <DIV> <DIV> <DIV> <DIV> <DIV> <H1 style=\"MARGIN: 17pt 0cm 16.5pt\"><FONT size=5><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">目</SPAN><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">录</SPAN></FONT></H1> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN lang=EN-US><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p>&nbsp;</o:p></SPAN><SPAN lang=EN-US><o:p>&nbsp;</o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">第一章</SPAN><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">绪论</SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">第一节</SPAN><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">人格的概述</SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">第二节</SPAN><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">君子的界说</SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">第三节</SPAN><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">君子人格设计的理论特色</SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">第二章</SPAN><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">君子人格与轴心时代</SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">第一节</SPAN><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">中国轴心时代的特殊性</SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">第二节</SPAN><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">君子概念的演进</SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">第三节</SPAN><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">君子人格在轴心时代的核心地位</SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">第四节</SPAN><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">圣人人格不是中国文化的核心</SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">第三章</SPAN><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">君子人格的品德</SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">第一节</SPAN><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">从君子比德看君子的品德</SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 21pt; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">第二节</SPAN><SPAN lang=EN-US><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">君子人格的