INSERT INTO `news` VALUES ('87', '〈人生哲理散论〉-----廖建平著', '<DIV> <DIV> <DIV> <DIV> <DIV> <DIV> <DIV> <DIV> <DIV class=Section1 style=\"LAYOUT-GRID:  15.4pt none; mso-layout-grid-char-alt: -1536\"> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">作者简介</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 23.25pt; mso-char-indent-count: 2.0\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">廖建平，男，湖南省衡南县人，生于</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">1963</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">年</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">2</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">月，</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\">1986</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: 宋体; mso-hansi-font-family: \'Times New Roman\'; mso-ascii-font-family: \'Times New Roman\'\">年毕业于湖南师范大学政治系，现工作于衡阳师范学院法学院，主要从事中国哲学和伦理学的教学与研究，著有《君子人格论》。</SPAN><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p></o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN lang=EN-US style=\"FONT-SIZE: 12pt\"><o:p>&nbsp;</o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN lang=EN-US><o:p>&nbsp;</o:p></SPAN></P> <P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN lang=EN-US><o:p>&nbsp;</o:p></SPAN></P></DIV><SPAN lang=EN-US style=\"FONT-SIZE: 10.5pt; FONT-FAMILY: \'Times New Roman\'; mso-font-kerning: 1.0pt; mso-bidi-font-size: 12.0pt; mso-fareas